SELECT

FROM messages
WHERE conversation_id = #{conversationId, javaType=java.util.UUID, jdbcType=OTHER}
ORDER BY created_at ASC

    LIMIT #{limit, jdbcType=INTEGER} OFFSET #{offset, jdbcType=INTEGER}

SELECT

FROM messages
WHERE conversation_id = #{conversationId, javaType=java.util.UUID, jdbcType=OTHER}
AND role = #{role, jdbcType=VARCHAR}
