mapper.select:
SELECT

FROM pr_snapshots
WHERE id = #{id, javaType=java.util.UUID, jdbcType=OTHER}

SELECT

FROM pr_snapshots
WHERE conversation_id = #{conversationId, javaType=java.util.UUID, jdbcType=OTHER}
AND file_path = #{filePath, jdbcType=VARCHAR}
LIMIT 1

SELECT

FROM pr_snapshots
WHERE conversation_id = #{conversationId, javaType=java.util.UUID, jdbcType=OTHER}
AND file_path LIKE #{filePathPattern, jdbcType=VARCHAR}
ORDER BY created_at DESC

mapper.delete:
DELETE
FROM pr_snapshots
WHERE id = #{id, javaType=java.util.UUID, jdbcType=OTHER}

DELETE
FROM pr_snapshots
WHERE conversation_id = #{conversationId, javaType=java.util.UUID, jdbcType=OTHER}

